            case 'ST_CONT_HABILITADO': return 'Habilitado';
            case 'ST_CONT_SUSP_CANCELAMENTO': return 'Suspenso por cancelamento';
            case 'ST_CONT_SUSP_DEBITO': return 'Suspenso por d√©bito';
            case 'ST_CONT_SUSP_SOLICITACAO': return 'Suspenso por solicita√ß√£o';
            case 'SUSP_DEBITO': return 'Suspenso por d√©bito';
            case 'SUSP_SOLICITACAO': return 'Suspenso por solicita√ß√£o';
            case 'HABILITADO': return 'Habilitado';
            case 'AGUARDANDO_TAXA_INST': return 'Aguardando taxa de instala√ß√£o';
            case 'EM_ATIVACAO': return 'Em ativa√ß√£o';
            case 'SUSP_CANCELAMENTO': return 'Suspenso por cancelamento';
            case 'CANCELADO': return 'Cancelado';
            default: return statusCode; // Retorna o pr√≥prio c√≥digo se n√£o reconhecido
        }
    }

    // Monta mensagem, bot√µes, etc
    for (let i = 0; i < objeto.length; i++){
        // Pega rota dos valores no JSON
        let street = objeto[i].street;
        let number = objeto[i].number;
        let bairro = objeto[i].bairro;
        let id = objeto[i].id;
        let status = mapeiaStatus(objeto[i].status_description); // Usa fun√ß√£o de mapeamento
        let cep = objeto[i].cep;
        let businessModel = objeto[i].business_model;
        let cluster = objeto[i].Cluster;
        let subBrand = objeto[i].sub_brand;

        let index = i+1;

        // Modela mensagem e atribui botoes
        resp.mensagem += geraTemplate(index, street, number, bairro, status)+"\n\n";
        resp.botoes.push((i+1).toString())
        let elem = geraTreinamento(index, street, number, bairro, status, cluster, subBrand)
        resp.listSimilaridade.push(elem)
    }
    if(vars.contratosCancelados.length > 0){
        resp.mensagem += "0 - Para contratos cancelados"
        resp.botoes.push("0")
        resp.listSimilaridade.push(["0"])
    }
    return resp;
}


