function geraListaDinamica(objeto, template, treinamento){
	//Define objeto de sa√≠da
	let resp = {
		mensagem: 'Achei essas faturas aqui:\n\n',
		botoes : [],
		listSimilaridade : []
	}
	//lookup table de meses
	const meses = [
        "Janeiro", "Fevereiro", "Mar√ßo", "Abril", "Maio", "Junho",
        "Julho", "Agosto", "Setembro", "Outubro", "Novembro", "Dezembro"
    ];
	//Monta mensagem, bot√µes, etc
	for (let i = 0; i<objeto.length; i++){
		//pega rota dos valores no JSON
		let auxData = new Date(objeto[i].expiration_date) || "sem data";
		let valor = objeto[i].value.toLocaleString("pt-br", {style: "currency", currency: "BRL"}) || "sem valor";
		let descricao = objeto[i].descri√ß√£o || "sem descri√ß√£o";
		let id = objeto[i].id || "sem id";
		let pix = objeto[i].pix || "sem pix";

		let index = i+1;
		let data = auxData.toLocaleDateString('pt-br') || "sem dia";
		let mes = meses[auxData.getMonth()-1] || "sem m√™s";
		let ano = auxData.getFullYear();
		let mesNumero = ("0" + (auxData.getMonth() + 1)).slice(-2);
		let diaNumero = (auxData.getDate() < 10 ? '0' : '')
                        + auxData.getDate();
	
		//modela mensagem e atribui botoes
		resp.mensagem += geraTemplate(index, data, valor, descricao, pix, id, mes, auxData, ano, mesNumero, diaNumero)+"\n\n";
		resp.botoes.push((i+1).toString())
		elem = geraTreinamento(index, data, valor, descricao, pix, id, mes)
		resp.listSimilaridade.push(elem)
	}
	return resp;
}

//Onde user pode definir como vai ser gerado a listagem. O modelo ou template
function geraTemplate(index, data, valor, descricao, pix, id, mes, auxData, ano, mesNumero, diaNumero){
	return `*${index}* - ${diaNumero}/${mesNumero}/${ano.toString().substring(2)} - ${valor}`;
	//return `${index}- Fatura do dia ${dia}\nValor: ${valor}`
}

//Onde user pode definir quais parametros vao ser levados para o treinamento da similaridade
function geraTreinamento(index, data, valor, descricao, pix, id, mes){
	return [index.toString()];
}

