const findFirstTwoAvailable = (days) => {
    const results = [];
    const addedDays = new Set(); // Para rastrear dias adicionados

    for (let day of days) {
        const { periods_availability } = day;

        if (!addedDays.has(day.day)) {
            if (periods_availability.morning === "available" && !isDateInList(day.day, "morning")) {
                results.push({ dayFormatted: formatDateToDDMMYYYY(day.day), day: day.day, periodFormatted: "manh√£", period: "morning" });
                addedDays.add(day.day);
            } else if (periods_availability.afternoon === "available" && !isDateInList(day.day, "afternoon")) {
                results.push({ dayFormatted: formatDateToDDMMYYYY(day.day), day: day.day, periodFormatted: "tarde", period: "afternoon" });
                addedDays.add(day.day);
            } else if (periods_availability.evening === "available" && !isDateInList(day.day, "evening")) {
                results.push({ dayFormatted: formatDateToDDMMYYYY(day.day), day: day.day, periodFormatted: "noite", period: "evening" });
                addedDays.add(day.day);
            }
        }

        if (results.length === 2) break; // Stop when we have two results
    }
    return results;
};

