function geraListaDinamica(objeto, template, treinamento){
    // Define objeto de sa√≠da
    let resp = {
        mensagem: 'Tenho mais duas datas dispon√≠veis:\n\n',
        botoes : [],
        listSimilaridade : []
    }

    // Monta mensagem, bot√µes, etc
    for (let i = 0; i < objeto.length; i++){
        // Pega rota dos valores no JSON
        let day = objeto[i].day;
        let dayFormatted = objeto[i].dayFormatted;
        let period = objeto[i].period;
        let periodFormatted = objeto[i].periodFormatted;

        let index = i+1;

        // Modela mensagem e atribui botoes
        resp.mensagem += geraTemplate(index, dayFormatted, periodFormatted)+"\n\n";
        resp.botoes.push((i+1).toString())
        let elem = geraTreinamento(index, dayFormatted, periodFormatted, day, period)
        resp.listSimilaridade.push(elem)
    }

    resp.mensagem += "Qual √© a melhor data para voc√™?"
    resp.botoes.push("Nenhuma delas")
    resp.listSimilaridade.push(["Nenhuma delas"])
    return resp;
}


