    function geraListaCondominios(objeto, template, treinamento) {
        let resp = {
            mensagem: 'Escolha o seu condom√≠nio üè¢:\n\n',
            botoes: [],
            listSimilaridade: []
        }

        const total = objeto.length;
        let listaFinal = [];

        if (total <= 9) {
            listaFinal = objeto;
        } else {
            // Pega primeiro, 7 do meio, e o √∫ltimo = 9 no total
            listaFinal = [
                objeto[0],
                ...objeto.slice(1, 8),
                objeto[total - 1]
            ];
        }

        for (let i = 0; i < listaFinal.length; i++) {
            const item = listaFinal[i];
            const name = item.name;
            const full_code = item.full_code;

            let blocos = (item.blocks || []).map(b => `${b.name} - ${b.full_code}`).join(' | ');

            const index = i + 1;

            resp.mensagem += template(index, name) + "\n";
            resp.botoes.push(index.toString());
            resp.listSimilaridade.push(treinamento(index, name, full_code, blocos));
        }

        // Adiciona a op√ß√£o "0 - Nenhum desses"
        resp.mensagem += "\n0 - Nenhum desses";
        resp.botoes.push("0");
        resp.listSimilaridade.push(["0"]);

        return resp;
    }

    function geraTemplateCondominio(index, name) {
        return `${index} - ${name}`;
    }

    function geraTreinamentoCondominio(index, name, full_code, blocos) {
        return [index.toString(), name, full_code, blocos];
    }

    let resultado = geraListaCondominios(vars.condominios, geraTemplateCondominio, geraTreinamentoCondominio);

    vars.mensagem = resultado.mensagem;
    vars.botoes = resultado.botoes;
    vars.treinamento = resultado.listSimilaridade;
    // #PARSER#CONNECTOR#Sa√≠da#
}

