const statusCode = `$statusCode`;
const statusMessage = `$statusMessage`;

function formataData(stringData) {
  const date = new Date(stringData);
  return date.toLocaleDateString('pt-BR');
}

msg(statusCode);

if (statusCode == 200) {

  const listaFaturas = [];

  resp.negotiation.invoices.forEach((invoice, index) => {
    const dataFormatada = formataData(invoice.expiration_date)
    const faturaString = `${index + 1} - ${dataFormatada} - R$ ${invoice.value.toFixed(2)}`;

    listaFaturas.push(faturaString);
  });

  //VARS DA DIVIDA ATUAL
  vars.listaFaturas = listaFaturas.join('\n');
  vars.valorTotal = resp.negotiation.original_value;

  //VARS A VISTA
  vars.aVista = resp.negotiation.parcels.filter(parcela => parcela.quantity === 1)[0];

  vars.valoraVista = vars.aVista.total_value;
