      vars.msg += `Valor: ${valor}\n`;
      vars.msg += `Situa√ß√£o: ${correspondencias[fatura.status]}`;
      if (indice <= (ultimasFaturas.length - 1)) vars.msg += "\n\n";
      vars.i++;
