function dataAtualComHifen(dataAntiga) {
    antigoArray = dataAntiga.split("/");
    novoArray = antigoArray.join("-");
    return novoArray;
}

function calculaData(dataAntes) {
    partesData = dataAntes.split("-");
    anoDataAtual = partesData[2];
    novoAno = (Number(anoDataAtual) - 2).toString();
    partesData[2] = novoAno;
    novaDataFormatada = partesData.join("-");
    return novaDataFormatada;
}

