// --------- FUN√á√ÉO PARA OBTER STATUS E DATA DA √öLTIMA FATURA ----------
function obterStatusEDataUltimaFatura(faturas) {
  if (!Array.isArray(faturas) || faturas.length === 0) {
    return null;
  }

  const statusMap = {
    0: 'Normal',
    1: 'Cancelado',
    2: 'Processado',
    3: 'Desfeito',
    4: 'Estornado'
  };

  // Determina a "data efetiva" (string ou Date) usada para ordena√ß√£o
  const pegarBaseData = (f) => {
    const isPix = String(f?.nomeEstabelecimento || '').toUpperCase() === 'PIX';
    const candidata = isPix
      ? (f?.dataHoraPagamento || f?.dataHoraEntradaPagamento)
      : (f?.dataHoraEntradaPagamento || f?.dataHoraPagamento);
    return candidata ?? null;
  };

  // Converte para Date v√°lida (ou null)
  const toValidDate = (x) => {
    if (!x) return null;
    const d = new Date(x);
    return isNaN(d.getTime()) ? null : d;
  };

  // Filtra somente faturas com data v√°lida
  const comDataValida = faturas
    .map((f) => {
      const baseStr = pegarBaseData(f);
      const baseDate = toValidDate(baseStr);
      return baseDate ? { fatura: f, baseStr, baseDate } : null;
    })
    .filter(Boolean);

  if (comDataValida.length === 0) return null;

  // Ordena por data desc
  comDataValida.sort((a, b) => b.baseDate - a.baseDate);

  const { fatura: ultima, baseStr, baseDate } = comDataValida[0];

  // Formata datas de forma est√°vel
  const yyyy = String(baseDate.getFullYear());
  const mm = String(baseDate.getMonth() + 1).padStart(2, '0');
  const dd = String(baseDate.getDate()).padStart(2, '0');

  const dataISO = `${yyyy}-${mm}-${dd}`; // "YYYY-MM-DD" em hor√°rio local
  const dataPTBR = baseDate.toLocaleDateString('pt-BR', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric'
  });

  const statusDescricao = statusMap?.[ultima?.status] ?? 'Desconhecido';

  return {
    status: ultima?.status ?? null,
    descricao: statusDescricao,
    dataISO,
    dataPTBR,
    fatura: ultima,
    baseStr
  };
}
